mapper.select:
SELECT c.CollectId, c.CollectType, c.UserId, c.CodeId, c.LiterId, c.DataId, c.VideoId, CASE WHEN c.CodeId IS NOT NULL THEN co.CodeName WHEN c.LiterId IS NOT NULL THEN li.LiterName WHEN c.DataId IS NOT NULL THEN da.DataName WHEN c.VideoId IS NOT NULL THEN vi.VideoName END AS DataName, CASE WHEN c.CodeId IS NOT NULL THEN co.UploadTime WHEN c.LiterId IS NOT NULL THEN li.UploadTime WHEN c.DataId IS NOT NULL THEN da.UploadTime WHEN c.VideoId IS NOT NULL THEN vi.UploadTime END AS UploadTime, -- 获取与收藏相关的团队名称
u.UserName FROM collect c LEFT JOIN code co ON c.CodeId = co.CodeId LEFT JOIN literature li ON c.LiterId = li.LiterId LEFT JOIN datasets da ON c.DataId = da.DataId LEFT JOIN video vi ON c.VideoId = vi.VideoId LEFT JOIN user u ON CASE WHEN c.CodeId IS NOT NULL THEN co.UserId WHEN c.LiterId IS NOT NULL THEN li.UserId WHEN c.DataId IS NOT NULL THEN da.UserId WHEN c.VideoId IS NOT NULL THEN vi.UserId END = u.UserId WHERE c.UserId = #{userId} AND (#{selectedCategory} IS NULL OR c.CollectType = #{selectedCategory})
LIMIT #{start}, #{limit}

select

from collect
where DataId = #{dataid,jdbcType=INTEGER}
and UserId = #{userid,jdbcType=INTEGER}

select

from collect
